body.script:
        var aku = 20;
        var kamu = 19;
        var hasil;

        // sama dengan
        hasil = aku == kamu;
        document.write(`${aku} == ${kamu} → ${hasil}
`);

        // lebih besar
        hasil = aku > kamu;
        document.write(`${aku} > ${kamu} → ${hasil}
`);

        // lebih kecil
        hasil = aku < kamu;
        document.write(`${aku} < ${kamu} → ${hasil}
`);

        // lebih besar sama dengan
        hasil = aku >= kamu;
        document.write(`${aku} >= ${kamu} → ${hasil}
`);

        // lebih kecil sama dengan
        hasil = aku <= kamu;
        document.write(`${aku} <= ${kamu} → ${hasil}
`);

        // tidak sama dengan
        hasil = aku != kamu;
        document.write(`${aku} != ${kamu} → ${hasil}
`);
